-- Create autoincrementing columns with
-- sequences or making them identities
create sequence seq;

create table t (
  c1 varchar2(10),
  c2 int 
    generated by default on null as identity,
  c3 int 
    default on null seq.nextval
);

insert into t values ( 
  'DEFAULTS', default, default 
);

insert into t values ( 
  'NULLS', null, null  
);

insert into t ( c1 ) values ( 
  'OMIT' 
);

select * from t;

C1                 C2         C3
---------- ---------- ----------
DEFAULTS            1          1
NULLS               2          2
OMIT                3          3
