create or replace json relational duality view department_employees as
select json { 'dId' : department_id, 'dName' : department_name, 'emps' : [
   ( select json { 'eName' : last_name, 'eId' : employee_id, 'hired' : hire_date }
     from   employees e with insert update delete
     where  e.department_id = d.department_id )
   ] } 
from   departments d with insert update delete;

