-- Create a SQL domain with type contraint
create DOMAIN Celsius as number 
   constraint is_above_absolute_zero CHECK (Celsius >= -273.15)
   DISPLAY Celsius || '°C';

create table Readings (
    id          number,
    temperature DOMAIN Celsius -- column inherits SQL Domain
);

-- Violates domain constraint
insert into Readings values ( 1, -9999 );

ORA-02290: check constraint (CHRIS.SYS_C008486) violated

