-- Create a multi-column domain
Create DOMAIN Customer as (
  f_n as varchar2(100 char), l_n as varchar2(100 char) 
) DISPLAY initcap(f_n) || ' ' || initcap(l_n);

Create table customers (
    first_name varchar2(100 char),
    last_name varchar2(100 char),
    -- SQL Domain applied on 2 columns
    DOMAIN Customer(first_name, last_name)
);

insert into customers (first_name, last_name) 
values( 'loïc', 'lefèvre' );
commit;
                                                                 TITLE
select DOMAIN_DISPLAY( first_name, last_name ) as title   -------------        
  from customers;                                          Loïc Lefèvre
