-- On successful conversion, returns 1
SELECT VALIDATE_CONVERSION('04-Apr-22 14:24:00' AS TIMESTAMP,
                           'DD-MON-YY HH24:MI:SS') FROM DUAL;


-- On failed conversion, returns 0
SELECT VALIDATE_CONVERSION('$29.99' AS BINARY_FLOAT) FROM DUAL;


-- Loads BAD data from external table only if conversion fails
INSERT INTO error_table
SELECT created_date FROM external_table
 WHERE VALIDATE_CONVERSION(created_date AS DATE, 'dd-MON-yyyy') = 0;
